import React from 'react';

var firebase = require("firebase/app");
require('firebase/auth');
require('firebase/firestore');

const firebaseConfig = {
  apiKey: "AIzaSyBv8L5c8THfMUb95DPsK0-KJz4lfIvjewo",
  authDomain: "tweet2shirt-4c967.firebaseapp.com",
  databaseURL: "https://tweet2shirt-4c967.firebaseio.com",
  projectId: "tweet2shirt-4c967",
  storageBucket: "tweet2shirt-4c967.appspot.com",
  messagingSenderId: "1042027584400",
  appId: "1:1042027584400:web:c93158799bdf5094563115",
  measurementId: "G-LXR2HCQ969"
};
firebase.initializeApp(firebaseConfig);

const db = firebase.firestore();

export class Product extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
            error: null,
            isLoaded: false,
            items: []
        };
        this.componentDidMount = this.componentDidMount.bind(this);
    }
    componentDidMount(){
        db.collection('products').get()
            .then(function(results){
                this.setState({
                    isLoaded: true,
                    items: results
                });
            })
    }
    render(){
        return(
            <h1>Hello</h1>
        )
    }
}
