/* global Stripe */
import React from 'react';
import './Product.css';

var firebase = require("firebase/app");
require('firebase/firestore');
firebase.initializeApp(require('../db.json'));
const db = firebase.firestore();
const stripe = Stripe('pk_test_H9E39lVcxUPU9ZJQJYad0dDi00YPeF5LjX');

export class Product extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
            error: null,
            isLoaded: false,
            product: {}
        };
        this.componentDidMount = this.componentDidMount.bind(this);
        this.handleClick = this.handleClick.bind(this);
    }
    componentDidMount(){
        let id = this.props.match.params.id;
        db.collection('products').where("id","==",id).get()
            .then((response) => response.docs[0].data())
            .then((data) => this.setState({product:data}))
    }
    handleClick(e){
        e.preventDefault();
        fetch('http://localhost:4242/create-checkout-session', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
              },
            body: JSON.stringify(this.state.product)
        })
        .then(function(response){
            return response.json();
        })
        .then(function(session){
            return stripe.redirectToCheckout({sessionId:session.id})
        })
        .then(function(result){
            if (result.error){
                alert(result.error.message);
            }
        })
        .catch(function(error){
            console.log('Error:',error);
        });
    }
    render(){
        return(
            <div className="Product">
                <img src={this.state.product.mockup}/>
                <h1>{this.state.product.name}</h1>
                <button onClick={this.handleClick}>Checkout</button>
            </div>
        )
    }
}
