import React from 'react';
import './Product.css';
import {CardElement, Elements, useElements, useStripe} from '@stripe/react-stripe-js';
import {loadStripe} from '@stripe/stripe-js';
var firebase = require("firebase/app");
require('firebase/firestore');

firebase.initializeApp(require('../db.json'));

const db = firebase.firestore();
const stripePromise = loadStripe("pk_test_H9E39lVcxUPU9ZJQJYad0dDi00YPeF5LjX");

export class Product extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
            error: null,
            isLoaded: false,
            product: {}
        };
        this.componentDidMount = this.componentDidMount.bind(this);
    }
    componentDidMount(){
        let id = this.props.match.params.id;
        db.collection('products').where("id","==",id).get()
            .then((response) => response.docs[0].data())
            .then((data) => this.setState({product:data}))
    }
    render(){
        return(
            <div class="Product">
                <img src={this.state.product.mockup}/>
                <h1>{this.state.product.name}</h1>
                <form>
                    <CardElement
                        options={{
                        style: {
                            base: {
                            fontSize: '16px',
                            color: '#424770',
                            '::placeholder': {
                                color: '#aab7c4',
                            },
                            },
                            invalid: {
                            color: '#9e2146',
                            },
                        },
                        }}
                    />
                    <button type="submit" disabled={!stripe}>
                        Pay
                    </button>
                </form>
            </div>
        )
    }
}
