/* global Stripe */
import React from 'react';
import './Product.css';
import {Elements} from '@stripe/react-stripe-js';
import {loadStripe} from '@stripe/stripe-js';
import CheckoutForm from './CheckoutForm';

var firebase = require("firebase/app");
require('firebase/firestore');
firebase.initializeApp(require('../db.json'));
const db = firebase.firestore();
const stripePromise = loadStripe('pk_test_51HQdWeASgcEJxMSWHww676gu5QpI4su56BeH7QOi5wS4H5aJO4YMA2AvLPfIfJGfW5anU82hweD1azW1iXZmeixH00DgWCGqk4');

export class Product extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
            error: null,
            isLoaded: false,
            product: {}
        };
        this.componentDidMount = this.componentDidMount.bind(this);
    }
    componentDidMount(){
        let id = this.props.match.params.id;
        db.collection('products').where("id","==",id).get()
            .then((response) => response.docs[0].data())
            .then((data) => this.setState({product:data}))
    }
    render(){
        return(
            <div className="Product">
                <img src={this.state.product.mockup}/>
                <h1>{this.state.product.name}</h1>
                <Elements stripe={stripePromise}>
                    <CheckoutForm stripe={stripePromise} />
                </Elements>
            </div>
        )
    }
}
